import './App.css';
import { useState } from "react";

function App() {
  const [htmlContent, setHtmlContent] = useState(""); 
  const [data, setData] = useState(null); 
  const [loading, setLoading] = useState(false); 
  const [error, setError] = useState(null); 

  const handleClick = async () => {
    setLoading(true);
    setError(null);

    try {
      const response = await fetch("http://127.0.0.1:5000/analyze-html", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ html: htmlContent }) 
      });

      if (!response.ok) {
        throw new Error("Failed to analyze HTML");
      }

      const result = await response.json();
      setData(result);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <header>
        <h1>ADA Compliance Checker</h1>
      </header>

      <div className="Container">
        <textarea 
          id="textbox" 
          placeholder="Enter HTML here..." 
          value={htmlContent}
          onChange={(e) => setHtmlContent(e.target.value)}
        />
        <div className="Container">
          {loading && <p>Loading...</p>}
          {error && <p style={{ color: "red" }}>{error}</p>}
          {data && <Results data={data} />}
        </div>
      </div>

      <div className="Container">
        <button onClick={handleClick}>Submit</button>
      </div>


    </div>
  );
}

function Results({ data }) {
  return (
    <div className="App" id="results">
      {typeof data.rule_id === "undefined" ? (
        <p>No analysis available</p>
      ) : (
        <>
          <p>"Placeholder"</p>
          <ul>
            <li>Element: {data.element}</li>
            <li>Details: {data.message}</li>
            <li>Rule: {data.rule_id}</li>
          </ul>
        </>
      )}
    </div>
  );
}

export default App;
