import './App.css';
import { useState } from "react";

function App() {
  const [htmlContent, setHtmlContent] = useState(""); 
  const [data, setData] = useState(null); 
  const [loading, setLoading] = useState(false); 
  const [error, setError] = useState(null); 

  const handleSubmit = async () => {
    setLoading(true);
    setError(null);

    // This inserts the user input into a preview div for markup
    const textarea = document.getElementById('textbox');
    const preview = document.getElementById('preview');
    const text = textarea.value;
    preview.innerText = text;


    try {
      const response = await fetch("http://127.0.0.1:5000/analyze-html", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ html: htmlContent }),
        // mode: 'no-cors' 
      });

      if (!response.ok) {
        throw new Error("Failed to analyze HTML");
      }

      const result = await response.json();
      setData(result);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <header>
        <h1>ADA Compliance Checker</h1>
      </header>

      <div className="Container">
        <textarea 
          id="textbox" 
          placeholder="Enter HTML here..." 
          value={htmlContent}
          onChange={(e) => setHtmlContent(e.target.value)}
        />
        <div id="preview" className="Card"></div>
        <div className="Container">
          {loading && <p>Loading...</p>}
          {error && <p style={{ color: "red" }}>{error}</p>}
          {data && <Results data={data} />}
        </div>
      </div>

      <div className="Container">
        <button onClick={handleSubmit}>Submit</button>
      </div>


    </div>
  );
}

function Results({ data }) {
  const originalText = useRef("");

  useEffect(() => {
    const preview = document.getElementById("preview");
    if (preview) originalText.current = preview.innerText; 
  }, []);

  return (
    <div className="Container" id="results">
        <>
          <ul>
            {data.map((error)=><ErrorCard key={error.name} error={error} originalText={originalText}/>)}
          </ul>
        </>
    </div>
  );
}

function ErrorCard({error, originalText}) {
  const errorTitles = {
    DOC_LANG_MISSING: "Missing Language Attribute",
    DOC_TITLE_MISSING: "Missing Document Title",
    COLOR_CONTRAST: "Low Contrast Ratio",
    IMG_ALT_MISSING: "Alternative Text",
    IMG_ALT_LENGTH: "Alternative Text Length",
    LINK_GENERIC_TEXT: "Meaningful Link Text",
    HEADING_ORDER: "Hierarchical Order",
    HEADING_MULTIPLE_H1: "Single <h1>"
  };

  const title = errorTitles[error.name]

  const handleClick = () => {    
    const preview = document.getElementById("preview");
    if (!preview) return;

    const target = error.selector
    if (!target) return;

    preview.innerHTML = originalText.current.replace(
      new RegExp(target, 'gi'),
      `<span style="background-color: yellow; border: 1px solid red;">${target}</span>`
    );
  }

   return (
    <div className="Card" onClick={handleClick}>
      <h3>{title}</h3>
      <ul>
        <li><strong>Element:</strong> {error.element}</li>
        <li><strong>Details:</strong> {error.message}</li>
        <li><strong>Rule:</strong> {error.name}</li>
       </ul>
    </div>   
   )
}


export default App;
