import './App.css';
import { useState } from "react";

function App() {
  const [htmlContent, setHtmlContent] = useState(""); 
  const [data, setData] = useState(null); 
  const [loading, setLoading] = useState(false); 
  const [error, setError] = useState(null); 

  const handleClick = async () => {
    setLoading(true);
    setError(null);

    try {
      const response = await fetch("http://127.0.0.1:5000/analyze-html", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ html: htmlContent }),
        // mode: 'no-cors' 
      });

      if (!response.ok) {
        throw new Error("Failed to analyze HTML");
      }

      const result = await response.json();
      setData(result);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <header>
        <h1>ADA Compliance Checker</h1>
      </header>

      <div className="Container">
        <textarea 
          id="textbox" 
          placeholder="Enter HTML here..." 
          value={htmlContent}
          onChange={(e) => setHtmlContent(e.target.value)}
        />
        <div className="Container">
          {loading && <p>Loading...</p>}
          {error && <p style={{ color: "red" }}>{error}</p>}
          {data && <Results data={data} />}
        </div>
      </div>

      <div className="Container">
        <button onClick={handleClick}>Submit</button>
      </div>


    </div>
  );
}

function Results({ data }) {
  return (
    <div className="App" id="results">
        <>
          <ul>
            {data.map((error)=><ErrorCard error={error}/>)}
          </ul>
        </>
      {/* )} */}
    </div>
  );
}

function ErrorCard(props) {
  const errorTitles = {
    COLOR_CONTRAST: "Low Contrast Ratio",
    DOC_TITLE_MISSING: "Missing Document Title",
    DOC_LANG_MISSING: "Missing Language Attribute",
  };

  const title = errorTitles[error.name]

   return (
    <div className="p-4 rounded-2xl shadow-md border w-64 bg-white">
      <h3>{title}</h3>
      <ul>
        <li>{props.error.element}</li>
        <li>{props.error.message}</li>
        <li>{props.error.name}</li>
       </ul>
    </div>   
   )
}


export default App;
