import './App.css';
import { useState } from "react";

function App() {
  const [htmlContent, setHtmlContent] = useState(""); // stores textarea input
  const [data, setData] = useState(null); // will hold backend response
  const [loading, setLoading] = useState(false); // show loading state
  const [error, setError] = useState(null); // handle errors

  // Called when the user clicks Submit
  const handleClick = async () => {
    setLoading(true);
    setError(null);

    try {
      const response = await fetch("http://127.0.0.1:5000/analyze-html", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ html: htmlContent }) // send textarea content
      });

      if (!response.ok) {
        throw new Error("Failed to analyze HTML");
      }

      const result = await response.json();
      setData(result);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <header>
        <h1>ADA Compliance Checker</h1>
      </header>

      <div className="Container">
        <textarea 
          id="textbox" 
          placeholder="Enter HTML here..." 
          value={htmlContent}
          onChange={(e) => setHtmlContent(e.target.value)}
        />
      </div>

      <div className="Container">
        <button onClick={handleClick}>Submit</button>
      </div>

      <div className="Container">
        {loading && <p>Loading...</p>}
        {error && <p style={{ color: "red" }}>{error}</p>}
        {data && <Results data={data} />} {/* Pass data to Results */}
      </div>
    </div>
  );
}

// Results component now receives data as a prop
function Results({ data }) {
  return (
    <div className="App" id="results">
      {typeof data.word_count === "undefined" ? (
        <p>No analysis available</p>
      ) : (
        <>
          <p>Word Count: {data.word_count}</p>
          <p>Tag Count: {data.tag_count}</p>
          <p>Tags: {data.tags.join(", ")}</p>
        </>
      )}
    </div>
  );
}

export default App;
